$(window).load(function(){var a=0;$(".img-lazy-load").each(function(){var b=$(this);if(b.data("parent-id")){$("#"+b.data("parent-id")).append('<img id="img_highQuality_'+a+'" src="'+b.data("full-src")+'" style="display:none;">')}else{b.append('<img id="img_highQuality_'+a+'" src="'+b.data("full-src")+'" style="display:none;">')}$("#img_highQuality_"+a).off().on("load",function(){if(!b.hasClass("no-background")){b.css({"background-image":"url("+$(this).attr("src")+")"})}if(b.attr("src")!==""){b.attr("src",$(this).attr("src"))}});a++})});var classNames=[];if(navigator.userAgent.match(/(iPad|iPhone|iPod)/i)){classNames.push("device-ios")}if(navigator.userAgent.match(/android/i)){classNames.push("device-android")}var html=document.getElementsByTagName("html")[0];if(classNames.length){classNames.push("on-device")}if(html.classList){html.classList.add.apply(html.classList,classNames)};